// Title: Rearrange an array in maximum minimum form | Set 1

// Subject: Given a sorted array of positive integers, rearrange 
// the array alternately i.e first element should be maximum value, 
// second minimum value, third second max, fourth second min and so on. 

// Example:
// Input: arr[] = {1, 2, 3, 4, 5, 6, 7} 
// Output: arr[] = {7, 1, 6, 2, 5, 3, 4}

// Input: arr[] = {1, 2, 3, 4, 5, 6} 
// Output: arr[] = {6, 1, 5, 2, 4, 3} 

function reArrangeMaxMin(arr=[]){
    arrlen = arr.length;
    arr = arr.sort((a,b)=>a-b);
    let temp = [];
    let max = arr.length -1;

    for(let i = 0, j = max; i <= j; i++, j--){
       if(i != j){
        temp.push(arr[j]);
       }
        temp.push(arr[i]);    
    }
    return temp;
  
}

reArrangeMaxMin([1, 2, 3, 4, 5, 6, 7]); // 7, 1, 6, 2, 5, 3, 4 

reArrangeMaxMin([1, 2, 3, 4, 5, 6]); // 6, 1, 5, 2, 4, 3 